import React, { useState } from "react";
import SearchBar from "./SearchBar";
import WeatherWidget from "./WeatherWidget";

const Dashboard = () => {
  const [weatherData, setWeatherData] = useState(null);

  const handleSearch = async (city) => {
    try {
      const response = await fetch(`http://localhost:5000/api/weather?city=${city}`);
      const data = await response.json();
      if (response.ok) {
        setWeatherData(data);
      } else {
        setWeatherData(null);
        alert(data.message || "City not found");
      }
    } catch (error) {
      console.error("Error fetching weather data:", error);
      alert("Failed to fetch weather data");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6">Weather Dashboard</h1>

      {/* Search Bar */}
      <SearchBar onSearch={handleSearch} />

      {/* Weather Widgets */}
      {weatherData && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6 w-full max-w-4xl">
          <WeatherWidget title="Temperature" value={`${weatherData.temp}°C`} />
          <WeatherWidget title="Humidity" value={`${weatherData.humidity}%`} />
          <WeatherWidget title="Wind Speed" value={`${weatherData.windSpeed} km/h`} />
        </div>
      )}
    </div>
  );
};

export default Dashboard;
