import { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";

const Dashboard = () => {
  const { accessToken, refreshAccessToken, logout } = useContext(AuthContext);
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState("New York");

  const fetchWeather = async () => {
    try {
      const { data } = await axios.get(`http://localhost:5000/api/weather?city=${city}`, {
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      setWeather(data);
    } catch (error) {
      if (error.response?.status === 401) {
        await refreshAccessToken();
        fetchWeather();
      } else {
        console.error("Error fetching weather:", error.response?.data || error.message);
      }
    }
  };

  useEffect(() => {
    fetchWeather();
  }, [city]);

  return (
    <div className="flex flex-col items-center p-6">
      <h1 className="text-2xl font-bold mb-4">Weather Dashboard</h1>

      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city"
        className="border p-2 rounded-md"
      />
      <button onClick={fetchWeather} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded">
        Get Weather
      </button>

      {weather && (
        <div className="mt-4 p-4 border rounded shadow-md">
          <h2 className="text-xl font-bold">{weather.city}</h2>
          <p>Temperature: {weather.temperature}Â°C</p>
          <p>Condition: {weather.condition}</p>
        </div>
      )}

      <button onClick={logout} className="mt-4 bg-red-500 text-white px-4 py-2 rounded">
        Logout
      </button>
    </div>
  );
};

export default Dashboard;
