import React, { useState } from "react";

const SearchBar = ({ onSearch }) => {
  const [city, setCity] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (city.trim() === "") return;
    onSearch(city);
  };

  return (
    <form onSubmit={handleSubmit} className="w-full max-w-lg flex">
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        className="w-full p-2 border rounded-l-md focus:outline-none"
      />
      <button type="submit" className="px-4 bg-blue-500 text-white rounded-r-md hover:bg-blue-600">
        Search
      </button>
    </form>
  );
};

export default SearchBar;
