import React, { useEffect, useState, useRef } from "react";
import {
  Send,
  ThumbsUp,
  ThumbsDown,
  ChevronDown,
  Search,
  Pencil,
  Save,
  // ListCollapse,
  // UnfoldVertical,
  X,
} from "lucide-react";
import { suggestedQuestions } from "./Utilities/Utilities";
import Skeleton from "./Components/Skeleton";
import { MoreVertical } from "lucide-react";
import Table from "./Components/Table";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [filteredSuggestions, setFilteredSuggestions] =
    useState(suggestedQuestions);
  const [editingSql, setEditingSql] = useState(null);
  const [editedSql, setEditedSql] = useState("");
  const [openAccordionIndex, setOpenAccordionIndex] = useState(null);
  const [activeTab, setActiveTab] = useState("ACE");
  const [isLoading, setIsLoading] = useState(false);
  const [isSQLSave, setIsSQLSave] = useState(false);
  const [saveMessage, setSaveMessage] = useState(false);
  const [feedback, setFeedback] = useState(null);
  const [expandedItems, setExpandedItems] = useState(new Set());
  const [showMenu, setShowMenu] = useState(false);
  const [suggestedTab, setSuggestedTab] = useState([]);

  const messagesEndRef = useRef(null);
  const getStorageKey = (tab) => `chatHistory_${tab}`;

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleExpandAll = () => {
    if (expandedItems.size === messages.length) {
      // If all are expanded, collapse all
      setExpandedItems(new Set());
    } else {
      // Expand all
      const allIndices = Array.from({ length: messages.length }, (_, i) => i);
      setExpandedItems(new Set(allIndices));
    }
  };

  const parseAceResponse = (responseString) => {
    try {
      let response = responseString;
      if (
        typeof responseString === "string" &&
        responseString.includes('"response"')
      ) {
        const parsedResponse = JSON.parse(responseString);
        response = parsedResponse.response;
      }

      const dataMatch = response.match(/<Data>\s*([\s\S]*?)\s*<\/Data>/);
      const queryMatch = response.match(/<Query>\s*([\s\S]*?)\s*<\/Query>/);

      let parsedData = [];
      if (dataMatch && dataMatch[1]) {
        try {
          const jsonData = JSON.parse(dataMatch[1]);

          // Handle case where data is in column arrays format
          if (jsonData && typeof jsonData === 'object' && !Array.isArray(jsonData)) {
            const keys = Object.keys(jsonData);
            const maxLength = Math.max(...keys.map(key => jsonData[key].length));

            // Transform column arrays into array of objects
            parsedData = Array.from({ length: maxLength }, (_, index) => {
              const rowObject = {};
              keys.forEach(key => {
                rowObject[key] = jsonData[key][index];
              });
              return rowObject;
            });
          } else if (Array.isArray(jsonData)) {
            // Handle case where data is already an array of objects
            parsedData = jsonData;
          }
        } catch (e) {
          console.error("Error parsing data:", e);
        }
      }

      let query = "";
      if (queryMatch && queryMatch[1]) {
        query = queryMatch[1].replace(/\\n/g, "\n").replace(/\\"/g, '"').trim();
      }

      return {
        data: parsedData,
        query: query,
      };
    } catch (error) {
      console.error("Error parsing response:", error);
      return {
        data: [],
        query: "",
      };
    }
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setShowSuggestions(false);
    if (!input.trim()) return;

    // Collapse all previous items
    setExpandedItems(new Set());

    const apiUrl = "https://3fw377uw77.execute-api.us-east-1.amazonaws.com/dev/multi-agent-grid-invokePCAAgent";
    const payload = {
      prompt: input,
      agent_type: activeTab,
    };
    const userMessage = { text: input, isUser: true };
    setMessages((prev) => {
      const newMessage = [...prev, userMessage];
      localStorage.setItem(getStorageKey(activeTab), JSON.stringify(newMessage));
      return newMessage;
    });

    setInput("");

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "*/*",
        },
        mode: "cors",
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      let parsedResponse;
      if (activeTab === "ACE") {
        parsedResponse = parseAceResponse(data.response);
        const botResponse = {
          isUser: false,
          activeTab: "result",
          sql: parsedResponse.query,
          data: parsedResponse.data,
          responseType: activeTab,
          responseStatus: "sucess",
        };
        setMessages((prev) => {
          const newMessage = [...prev, botResponse];
          // Expand the new message
          setExpandedItems(new Set([newMessage.length - 1]));
          setOpenAccordionIndex(newMessage.length - 1);
          localStorage.setItem(getStorageKey(activeTab), JSON.stringify(newMessage));
          return newMessage;
        });
      } else if (activeTab === "SNOWFLAKE") {
        parsedResponse = JSON.parse(data.response.body);
        const botResponse = {
          isUser: false,
          activeTab: "result",
          sql: parsedResponse.query,
          data: parsedResponse.results,
          responseType: activeTab,
          responseStatus: "sucess",
        };
        setMessages((prev) => {
          const newMessage = [...prev, botResponse];
          // Expand the new message
          setExpandedItems(new Set([newMessage.length - 1]));
          setOpenAccordionIndex(newMessage.length - 1);
          localStorage.setItem(getStorageKey(activeTab), JSON.stringify(newMessage));
          return newMessage;
        });
      } else {
        const botResponse = {
          isUser: false,
          activeTab: "result",
          data: data.response,
          responseType: activeTab,
          responseStatus: "sucess",
        };
        setMessages((prev) => {
          const newMessage = [...prev, botResponse];
          // Expand the new message
          setExpandedItems(new Set([newMessage.length - 1]));
          setOpenAccordionIndex(newMessage.length - 1);
          localStorage.setItem(getStorageKey(activeTab), JSON.stringify(newMessage));
          return newMessage;
        });
      }
    } catch (error) {
      let errorMessage = error.message;
      if (error.message.includes("Read timed out")) {
        errorMessage = "I need more time to process your request. Please try again.";
      }

      const botResponse = {
        text: errorMessage,
        isUser: false,
        activeTab: "result",
        sql: `Error occurred`,
        data: errorMessage,
        responseType: activeTab,
        responseStatus: "error",
      };
      setMessages((prev) => {
        const newMessage = [...prev, botResponse];
        // Expand the error message
        setExpandedItems(new Set([newMessage.length - 1]));
        setOpenAccordionIndex(newMessage.length - 1);
        localStorage.setItem(getStorageKey(activeTab), JSON.stringify(newMessage));
        return newMessage;
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleTabChange = (index, tab) => {
    setMessages((prevMessage) => {
      return prevMessage.map((msg, i) =>
        i === index ? { ...msg, activeTab: tab } : msg
      );
    });
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    const filtered = suggestedQuestions.filter((q) =>
      q.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredSuggestions(filtered);
    setShowSuggestions(value.length > 0);
  };

  const handleSuggestionClick = (suggestion) => {
    setInput(suggestion);
    setShowSuggestions(false);
  };

  const handleEditClick = (index, sql) => {
    setTimeout(() => {
      setEditingSql(index);
      setEditedSql(sql);
    }, 3000);
  };

  const handleSaveEdit = (index) => {
    const updatedMessages = [...messages];
    if (updatedMessages[index] && editedSql.trim()) {
      updatedMessages[index] = {
        ...updatedMessages[index],
        sql: editedSql,
      };
      setMessages(updatedMessages);
    }
    setEditingSql(null);
    setEditedSql("");
  };

  const handleCancelEdit = () => {
    setEditingSql(null);
    setEditedSql("");
  };

  const toggleAccordion = (index) => {
    setExpandedItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(index)) {
        newSet.delete(index);
      } else {
        newSet.add(index);
      }
      return newSet;
    });
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showMenu && !event.target.closest(".menu-container")) {
        setShowMenu(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [showMenu]);

  const handleSaveSQL = () => {
    setIsSQLSave(true);
    setTimeout(() => {
      setIsSQLSave(false);
      setSaveMessage(true);
    }, 3000);
    setTimeout(() => {
      setSaveMessage(false);
    }, 5000);
  };

  const handleFeedback = (type) => {
    setFeedback((prev) => (prev === type ? null : type));
  };

  useEffect(() => {
    setMessages([]);
    const storedMessages = localStorage.getItem(getStorageKey(activeTab));
    if (storedMessages) {
      const parsedMessages = JSON.parse(storedMessages);
      setMessages(parsedMessages);
      // Only expand the last message if there are any messages
      if (parsedMessages.length > 0) {
        setExpandedItems(new Set([parsedMessages.length - 1]));
      }
    }
  }, [activeTab]);



  useEffect(() => {
    const fetchConfig = async () => {

      try {
        const response = await fetch(
          "https://3fw377uw77.execute-api.us-east-1.amazonaws.com/dev/configuration"
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setSuggestedTab(JSON.parse(data.body));
      } catch (error) {
        //setError(error.message);
      } finally {
      }
    };
    fetchConfig();
  }, []);

  return (
    <div className="h-screen bg-gray-200 flex items-center justify-center">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-5xl h-[85vh] flex flex-col">
        <div className="px-6 py-4 border-b border-gray-200 bg-white rounded-t-lg flex justify-between">
          <div>
            <h1 className="text-xl font-semibold text-gray-800">
              Chat Assistant
            </h1>
            <p className="text-sm text-gray-500">Ask me anything </p>
            {/* <span style={{cursor:"pointer", marginLeft:"-1000px"}}><ListCollapse />
           <UnfoldVertical />
           </span> */}
          </div>
          <div className="relative menu-container">
            <button
              onClick={(e) => {
                e.stopPropagation();
                setShowMenu(!showMenu);
              }}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors duration-200"
            >
              <MoreVertical size={20} className="text-gray-600" />
            </button>
            {showMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg border border-gray-200 z-50">
                <button
                  type="button"
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    handleExpandAll();
                    setShowMenu(false);
                  }}
                  className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors duration-200"
                >
                  {expandedItems.size === messages.length
                    ? "Collapse All"
                    : "Expand All"}
                </button>
              </div>
            )}
          </div>
        </div>
        <div className="flex-1 p-4 overflow-y-auto scrollbar-none">
          {messages &&
            messages?.map((message, index) => (
              <div
                key={index}
                className={`mb-6 ${message?.isUser ? "flex justify-end" : "flex justify-start"
                  } message-animation`}
              >
                <div
                  className={`max-w-[80%] rounded-lg p-2 hover-scale ${message?.isUser
                      ? "bg-blue-600 text-white shadow-lg"
                      : "bg-white shadow-md hover:shadow-lg transition-shadow duration-300 border border-gray-300 rounded-lg"
                    }`}
                  style={{ width: !message?.isUser && "100%" }}
                >
                  {!message?.isUser &&
                    message?.responseStatus === "error" &&
                    message?.data && (
                      <div className="flex items-center gap-2 p-3 mt-2 bg-red-50 border border-red-200 rounded-md">
                        <svg
                          className="w-5 h-5 text-red-600"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"
                            d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                          />
                        </svg>
                        <span className="text-sm text-red-700 font-medium">
                          {message?.data}
                        </span>
                      </div>
                    )}

                  {!message?.isUser && message?.responseStatus !== "error" && (
                    <>
                      <div
                        onClick={() => toggleAccordion(index)}
                        className="p-2 cursor-pointer flex justify-between items-center  hover:bg-gray-50 transition-colors duration-200"
                      >
                        <div className="flex">
                          <button
                            className={`px-4 py-2 transition-all duration-200 ${message?.activeTab === "result"
                                ? "border-b-2 border-blue-500 text-blue-600"
                                : "text-gray-600 hover:text-blue-500"
                              }`}
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTabChange(index, "result");
                            }}
                          >
                            Result
                          </button>
                          <button
                            className={`px-4 py-2 transition-all duration-200 ${message?.activeTab === "sql"
                                ? "border-b-2 border-blue-500 text-blue-600"
                                : "text-gray-600 hover:text-blue-500"
                              }`}
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTabChange(index, "sql");
                            }}
                          >
                            SQL
                          </button>
                        </div>
                        <ChevronDown
                          size={20}
                          className={`transform transition-transform duration-200 ${openAccordionIndex === index ? "rotate-180" : ""
                            }`}
                        />
                      </div>

                      {/* Accordion Content */}

                      <div
                        className={`transition-all duration-300 mt-2 ${expandedItems.has(index)
                            ? "max-h-[1000px] opacity-100"
                            : "max-h-0 opacity-0 overflow-hidden"
                          }`}
                      >
                        <div className="p-3 ">
                          {message?.activeTab === "result" ? (
                            <div>
                              <div className="text-gray-800 mb-4">
                                {message?.responseType === "ACE" || message?.responseType === "SNOWFLAKE" ? (
                                  Array.isArray(message?.data) ? (
                                    <Table data={message?.data} />
                                  ) : null
                                ) : (
                                  <pre className="bg-gray-50 p-3 rounded-md border border-gray-300 overflow-x-auto whitespace-pre-wrap">
                                    {typeof message?.data === "object"
                                      ? JSON.stringify(message?.data, null, 2)
                                      : String(message?.data)}{" "}
                                  </pre>
                                )}
                              </div>
                            </div>
                          ) : (
                            <div className="relative">
                              {editingSql === index ? (
                                <div className="relative">
                                  <textarea
                                    value={editedSql}
                                    onChange={(e) =>
                                      setEditedSql(e.target.value)
                                    }
                                    className="w-full h-32 p-3 bg-gray-50 rounded-md border border-gray-300 text-sm font-mono focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                                  />
                                  <div className="absolute top-2 right-2 flex gap-2">
                                    <button
                                      onClick={() => handleSaveEdit(index)}
                                      className="p-1 rounded-md bg-green-500 text-white hover:bg-green-600 transition-colors duration-200"
                                    >
                                      <Save size={16} onClick={handleSaveSQL} />
                                    </button>
                                    <button
                                      onClick={handleCancelEdit}
                                      className="p-1 rounded-md bg-gray-500 text-white hover:bg-gray-600 transition-colors duration-200"
                                    >
                                      <X size={16} />
                                    </button>
                                  </div>
                                </div>
                              ) : (
                                <pre className="bg-gray-50 p-3 rounded-md border border-gray-300 flex-1 overflow-x-auto scrollbar-none">
                                  <code className="text-sm text-gray-800">
                                    {message?.sql}
                                  </code>
                                </pre>
                              )}
                            </div>
                          )}

                          {/* Feedback Icons */}
                          <div className="flex gap-2 mt-4 text-gray-500">
                            <button
                              className={`hover:text-green-500 transition-colors duration-200 p-1 rounded-full hover:bg-green-50 ${feedback === "UP" ? "text-green-500" : ""
                                }`}
                            >
                              <ThumbsUp
                                onClick={() => handleFeedback("UP")}
                                size={18}
                              />
                            </button>
                            <button
                              className={`hover:text-red-500 transition-colors duration-200 p-1 rounded-full hover:bg-red-50 ${feedback === "Down" ? "text-red-500" : ""
                                }`}
                            >
                              <ThumbsDown
                                onClick={() => handleFeedback("Down")}
                                size={18}
                              />
                            </button>
                            {message?.activeTab === "sql" && !editingSql && (
                              <button
                                onClick={() =>
                                  handleEditClick(index, message?.sql || "")
                                }
                                className="hover:text-blue-500 transition-colors duration-200 p-1 rounded-full hover:bg-blue-50"
                              >
                                <Pencil size={18} />
                              </button>
                            )}
                            {message?.activeTab === "sql" && (
                              <>
                                {isSQLSave && (
                                  <p
                                    style={{ marginLeft: "500px" }}
                                    className="text-black-700"
                                  >
                                    Saving in Progress...
                                  </p>
                                )}
                                {saveMessage && (
                                  <p
                                    style={{ marginLeft: "480px" }}
                                    className="text-black-700"
                                  >
                                    Saved Successfully ✅
                                  </p>
                                )}
                              </>
                            )}
                          </div>
                        </div>
                      </div>
                    </>
                  )}

                  {message?.isUser && <div>{message?.text}</div>}
                </div>
              </div>
            ))}
          {isLoading && (
            <div className="w-full flex justify-start mb-6">
              <div className="max-w-[80%] w-full">
                <Skeleton />
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Form */}
        <div className="p-4 border-t bg-white shadow-lg">
          <form
            className="relative"
          // onSubmit={handleSubmit}
          >
            <div className="relative">
              <input
                type="text"
                value={input}
                disabled={isLoading}
                onChange={handleInputChange}
                placeholder="Type your question here..."
                className="w-full p-4 pr-24 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-200"
              />
              <button
                onClick={handleSubmit}
                type="submit"
                className="absolute right-2 top-1/2 -translate-y-1/2 bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 transition-colors duration-200 hover:shadow-md"
              >
                <Send size={20} />
              </button>
              <button
                type="button"
                className="absolute right-16 top-1/2 -translate-y-1/2 text-gray-500 p-2 hover:text-blue-600 transition-colors duration-200"
                onClick={() => setShowSuggestions(!showSuggestions)}
              >
                <ChevronDown size={20} />
              </button>
            </div>

            {/* Suggestions Dropdown */}
            <div className="absolute bottom-full w-full mb-2 bg-white rounded-lg shadow-xl border border-gray-200 suggestion-animation ">
              <div className="flex border-b border-gray-200">
                {suggestedTab?.map((e) => {
                  return (
                    <button
                      className={`px-4 py-2 transition-all duration-200 ${activeTab === e.agent
                          ? "border-b-2 border-blue-500 text-blue-600"
                          : "text-gray-600 hover:text-blue-500"
                        }`}
                      onClick={(event) => {
                        event.preventDefault();
                        event.stopPropagation();
                        setActiveTab(e?.agent);
                      }}
                    >
                      {e?.displayName}
                    </button>
                  );
                })}
                <button
                  className={`px-4 py-2 transition-all duration-200 ${activeTab === "others"
                      ? "border-b-2 border-blue-500 text-blue-600"
                      : "text-gray-600 hover:text-blue-500"
                    }`}
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    setActiveTab("others");
                  }}
                >
                  Coming Soon
                </button>
              </div>

              {showSuggestions && suggestedTab && (
                <div
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                  }}
                  className="max-h-[300px] overflow-y-auto scrollbar-none"
                >
                  {suggestedTab?.map(
                    (e) =>
                      activeTab === e.agent && (
                        <div key={e.agent}>
                          {e.questionList?.map((question, index) => (
                            <button
                              key={index}
                              type="button"
                              className="w-full text-left px-4 py-3 hover:bg-blue-50 flex items-center gap-2 transition-colors duration-200 border-b last:border-b-0 border-gray-100"
                              onClick={(e) => {
                                e.preventDefault();
                                e.stopPropagation();
                                handleSuggestionClick(question);
                              }}
                            >
                              <Search size={16} className="text-blue-500" />
                              <span className="text-gray-700">{question}</span>
                            </button>
                          ))}
                        </div>
                      )
                  )}

                  {/* Others Tab Content */}
                  {activeTab === "others" && (
                    <div>
                      {filteredSuggestions.length > 0 ? (
                        filteredSuggestions?.map((suggestion, index) => (
                          <button
                            key={index}
                            type="button"
                            className="w-full text-left px-4 py-3 hover:bg-blue-50 flex items-center gap-2 transition-colors duration-200 border-b last:border-b-0 border-gray-100"
                            onClick={(e) => {
                              e.preventDefault();
                              e.stopPropagation();
                              handleSuggestionClick(suggestion);
                            }}
                          >
                            <Search size={16} className="text-blue-500" />
                            <span className="text-gray-700">{suggestion}</span>
                          </button>
                        ))
                      ) : (
                        <div className="p-4 text-gray-500 text-center">
                          No suggestions found
                        </div>
                      )}
                    </div>
                  )}
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
