import React, { useCallback } from "react";
import { useDropzone } from "react-dropzone";

const FileUpload = () => {
  const onDrop = useCallback((acceptedFiles) => {
    console.log("Uploaded file:", acceptedFiles);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: ".xpt",
    multiple: false,
  });

  return (
    <div className="mt-6 p-6 bg-white shadow-md border rounded-lg">
      <h2 className="text-lg font-semibold">Upload XPT File</h2>
      <p className="text-sm text-gray-500 mt-1">Choose an XPT file</p>

      <div {...getRootProps()} className="border-2 border-dashed border-gray-300 mt-4 p-10 text-center cursor-pointer hover:bg-gray-100">
        <input {...getInputProps()} />
        {isDragActive ? (
          <p className="text-gray-700">Drop the file here...</p>
        ) : (
          <p className="text-gray-500">
            Drag and drop file here <br /> (Limit: 1GB per file - .XPT)
          </p>
        )}
      </div>

      <button className="mt-4 px-5 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
        Browse files
      </button>
    </div>
  );
};

export default FileUpload;
