import React, { useState } from "react";

const UploadSection = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  // Handle file selection
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  // Handle drag-and-drop
  const handleDrop = (event) => {
    event.preventDefault();
    const file = event.dataTransfer.files[0];
    setSelectedFile(file);
  };

  return (
    <div className="mt-6 bg-white p-6 shadow-md rounded-lg">
      <h3 className="text-lg font-semibold">Upload XPT File</h3>
      <p className="text-gray-600 text-sm">Choose an XPT file</p>

      {/* Drag and Drop Box */}
      <div
        className="mt-4 border-2 border-dashed border-gray-300 p-6 rounded-lg flex flex-col items-center justify-center cursor-pointer hover:border-blue-500 transition"
        onDragOver={(e) => e.preventDefault()}
        onDrop={handleDrop}
      >
        {selectedFile ? (
          <p className="text-gray-600 text-sm">{selectedFile.name}</p>
        ) : (
          <>
            <p className="text-gray-500 text-sm">Drag and drop file here</p>
            <p className="text-gray-400 text-xs">(Limit: 1GB per file - .XPT)</p>
          </>
        )}
      </div>

      {/* Browse Button */}
      <div className="mt-4">
        <label
          htmlFor="file-upload"
          className="bg-blue-600 text-white px-4 py-2 rounded-lg cursor-pointer hover:bg-blue-700 transition"
        >
          Browse files
        </label>
        <input
          id="file-upload"
          type="file"
          className="hidden"
          accept=".xpt"
          onChange={handleFileChange}
        />
      </div>
    </div>
  );
};

export default UploadSection;
